mapper.sql:
WHERE permissiondetail.id is not NULL
AND permissiondetail.deleted = #{deleted}
AND permissiondetail.created_at >= CONCAT(#{sdate}, ' 00:00:00')
AND CONCAT(#{fdate}, ' 23:59:59') >= permissiondetail.created_at
AND permissiondetail.permission_id = #{permissionId}
AND permissiondetail.target = #{target}
AND permissiondetail.func = #{func}
