mapper.select:
SELECT user.id , user.deleted , user.created_at as createdAt , user.modified_at as modifiedAt , user.username , user.code , user.name , user.nick , user.phone , user.birthyear , user.gender , user.region , user.rfrom , user.fcm
, user.amountcpoint , user.amountcticket , user.countquizuser , user.counttoonuser , user.countqtoonuser
, user.updatecpoint , user.allowalarm , user.alarmquizresult , user.alarmquiztoday FROM user WHERE user.id = #{id}

mapper.sql:
WHERE user.id is not NULL
AND user.deleted = #{deleted}
AND user.created_at >= CONCAT(#{sdate}, ' 00:00:00')
AND CONCAT(#{fdate}, ' 23:59:59') >= user.created_at
AND user.code = #{code}
AND user.name LIKE CONCAT('%', #{name}, '%')
AND user.birthyear = #{birthyear}
AND user.gender = #{gender}
AND user.region = #{region}
AND user.rfrom = #{rfrom}
AND user.fcm = #{fcm}
